    SELECT ROUND(Sehbeteiligung*1000,3) as Sehbeteiligung, ROUND(Marktanteil, 3) as Marktanteil,  Datum
    FROM `ndr-code-bi-dashboard.Quotenreport_Sample.${dataset}_Sendungsliste`
    ORDER by Datum ASC);
END IF;
