const sPreTimes = data.series.map(s => s.fields.find(f => f.name === 'pre_timestamp').values.buffer);
const series = data.series.map((s) => {
  const sData = s.fields.find((f) => f.name === 'visits_past_5_minutes').values.buffer || s.fields.find((f) => f.type === 'number').values.buffer;
  const sTime = s.fields.find((f) => f.name === 'time').values.buffer
  const type = s.fields.find((f) => f.name === 'type').values.buffer[0]
  const refId = s.fields.find((f) => f.name === 'refId').values.buffer[0]
  const glaettung = s.fields.find((f) => f.name === 'glaettung').values.buffer[0]
  const zeitraum = s.fields.find((f) => f.name === 'zeitraum').values.buffer[0]
  console.log("Glaettung", glaettung)
  console.log("REF ID", refId)
  console.log("Zeitraum", zeitraum)
  //const color = "Gätten" && refId === "Heute" ? "#f5ee1d" : "#241df5"
  let color;
  if (glaettung === "Glätten" && zeitraum === "Zeitraum" && type === "line") {
    color = "#6aaa96"; // heute // Light Petrol = #6aaa96 
  } else if (glaettung === "Glätten" && zeitraum != "Zeitraum" && type === "line") {
    color = "#004c6d"; // gestern //  Dark-Petrol #004c6d
  } else if (glaettung != "Glätten" && zeitraum === "Zeitraum" && type === "line") {
    color = "#6aaa96"; // heute // 
  } else if (glaettung != "Glätten" && zeitraum != "Zeitraum" && type === "line") {
    color = "#004c6d"; //gestern
  }
  else if (type === "bar" && zeitraum === "Zeitraum") {
    color = "#6aaa96";
  }
  else if (type === "bar" && zeitraum != "Zeitraum") {
    color = "#004c6d";
  }
  console.log("sTime", sTime)
  console.log("type", type)
  console.log("refId", refId)
  console.log("Sdata", sData)

  return {
    name: refId,
    type: type,
    smooth: true,
    showSymbol: false,
    areaStyle: {
      opacity: 0.9,
    },
    itemStyle: {
      color: color,
      width: 10
    },

    lineStyle: {
      width: 1,
      color: color
    },
    data: sData.map((d, i) => [sTime[i], d.toFixed(2)]),
  };
});

/**
 * Enable Data Zoom by default
 */
setTimeout(() => echartsInstance.dispatchAction({
  type: 'takeGlobalCursor',
  key: 'dataZoomSelect',
  dataZoomSelectActive: true,
}), 500);

/**
 * Update Time Range on Zoom
 */
echartsInstance.on('datazoom', function (params) {
  const startValue = params.batch[0]?.startValue;
  const endValue = params.batch[0]?.endValue;
  locationService.partial({ from: startValue, to: endValue });
});


return {
  backgroundColor: 'transparent',
  tooltip: {
    trigger: 'axis',
    formatter: function (params) {
      let tooltipContent = '';
      params.forEach(function (item) {
        const date = new Date(item.data[0]);
        const tenMinutesInMilliseconds = 10 * 60 * 1000; // 10 minutes in milliseconds
        const date_start = new Date(date.getTime() - tenMinutesInMilliseconds);
        console.log("Date start", date_start)
        const preDate = new Date(sPreTimes[item.seriesIndex][item.dataIndex]);
        const currentDate = new Date(item.data[0]); // current timestamp

        // Format times
        let preHours = preDate.getHours().toString().padStart(2, '0');
        let preMinutes = preDate.getMinutes().toString().padStart(2, '0');
        let currentHours = currentDate.getHours().toString().padStart(2, '0');
        let currentMinutes = currentDate.getMinutes().toString().padStart(2, '0');

        const intervalStart = preHours + ':' + preMinutes;
        const intervalEnd = currentHours + ':' + currentMinutes;
        const intervalString = intervalStart + '-' + intervalEnd;


        let number = Math.floor(item.data[1]);
        let formattedNumber = new Intl.NumberFormat('de-DE').format(number);

        if (item.componentSubType === "bar") {
          tooltipContent += item.marker + item.seriesName + " Visits: " + formattedNumber + '<br/>';
        }
        else {
          tooltipContent += item.marker + item.seriesName + " " + intervalString + " Visits: " + formattedNumber + '<br/>';
        }
      });
      return tooltipContent;
    }
  },
  legend: {
    left: '0',
    bottom: '0',
    data: data.series.map((s) => {
      const refId = s.fields.find((f) => f.name === "refId").values.buffer[0];
      return refId;
    }),
    textStyle: {
      color: 'rgba(128, 128, 128, .9)',
    },
  },
  toolbox: {
    feature: {
      dataZoom: {
        yAxisIndex: 'none',
        icon: {
          zoom: 'path://',
          back: 'path://',
        },
      },
      saveAsImage: {},
    }
  },
  xAxis: {
    type: 'time',
  },
  yAxis: {
    type: 'value',
    min: '0',
  },
  grid: {
    left: '2%',
    right: '2%',
    top: '2%',
    bottom: 24,
    containLabel: true,
  },
  series,
};
