function formatDate(timestamp) {
  const date = new Date(timestamp);
  let day = ('0' + date.getDate()).slice(-2);
  let month = ('0' + (date.getMonth() + 1)).slice(-2);
  let year = date.getFullYear();
  return `${day}.${month}.${year}`;
}

const selectedTimeRange = context.panel.data.series[0].fields.find(f => f.name === 'timeRange').values[0];

const series = context.panel.data.series.map(s => {
  const timeValues = s.fields.find((f) => f.name === 'time').values; //|| s.fields.find(f => f.type === 'time').values;
  const searchValues = s.fields.find((f) => f.name === 'search').values;
  const discoverValues = s.fields.find((f) => f.name === 'discover').values;
  const newsValues = s.fields.find((f) => f.name === 'news').values;
  const totalValues = s.fields.find((f) => f.name === 'total').values;

  return [
    {
      name: 'Websuche',
      type: 'line',
      smooth: true,
      showSymbol: false,
      data: searchValues.map((value, index) => [timeValues[index], value])
    },
    {
      name: 'Discover',
      type: 'line',
      smooth: true,
      showSymbol: false,
      data: discoverValues.map((value, index) => [timeValues[index], value])
    },
    {
      name: 'Google News',
      type: 'line',
      smooth: true,
      showSymbol: false,
      data: newsValues.map((value, index) => [timeValues[index], value])
    },
    {
      name: 'Gesamt',
      type: 'line',
      smooth: true,
      showSymbol: false,
      data: totalValues.map((value, index) => [timeValues[index], value])
    }
  ];
}).flat();


/**
 * Enable Data Zoom by default
 */
setTimeout(() => context.panel.chart.dispatchAction({
  type: 'takeGlobalCursor',
  key: 'dataZoomSelect',
  dataZoomSelectActive: false,
}), 500);

/**
 * Update Time Range on Zoom
 */
context.panel.chart.on('datazoom', function (params) {
  const startValue = params.batch[0]?.startValue;
  const endValue = params.batch[0]?.endValue;
  context.grafana.locationService.partial({ from: startValue, to: endValue });
});


return {
  backgroundColor: 'transparent',
  tooltip: {
    trigger: 'axis',
    axisPointer: {
      type: 'none'
    },
    formatter: function (params) {
      let tooltipContent = formatDate(params[0].axisValue) + '<br/>'; // Datumsformatierung
      params.forEach(function (item) {
        let formattedValue = item.data[1].toString().replace(/\B(?=(\d{3})+(?!\d))/g, '.'); // Tausendertrennzeichen einfügen
        tooltipContent += item.marker + item.seriesName + ': ' + formattedValue + '<br/>';
      });
      return tooltipContent;
    }
  },
  legend: {
    left: '0',
    bottom: '10',
    data: context.panel.data.series.map((s) => {
      const searchValues = s.fields.find((f) => f.name === 'search').values;
      const discoverValues = s.fields.find((f) => f.name === 'discover').values;
      const newsValues = s.fields.find((f) => f.name === 'news').values;
      const totalValues = s.fields.find((f) => f.name === 'total').values;;
      return searchValues, discoverValues, newsValues, totalValues;
    }),
    textStyle: {
      color: 'rgba(128, 128, 128, .9)',
    },
  },
  toolbox: {
    feature: {
      dataZoom: {
        yAxisIndex: 'none',
        icon: {
          zoom: 'path://',
          back: 'path://',
        },
      },
      saveAsImage: {},
    }
  },
  xAxis: {
    type: 'category',
    axisLabel: {
      hideOverlap: true,
      formatter: function (value) {
        const date = new Date(value);
        let day = ('0' + date.getDate()).slice(-2);
        let month = ('0' + (date.getMonth() + 1)).slice(-2);
        let year = date.getFullYear();
        return `${day}.${month}.${year}`;
      },
      showMaxLabel: selectedTimeRange === "7 Tage"
    },
  },
  yAxis: {
    type: 'value',
    min: '0',
    axisLabel: {
      formatter: function (value) {
        // Umwandeln des Werts in einen String und Ersetzen von Kommas durch Punkte
        return value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, '.');
      }
    },
  },
  grid: {
    left: '2%',
    right: '2%',
    top: '2%',
    bottom: 24,
    containLabel: true,
  },
  series,
};

