IF (("${LRA:text}" LIKE "Sender" AND "${Sendung:text}" NOT LIKE "%DR Fernsehen") OR "${LRA:text}" LIKE "ARD" OR "${special_region}" NOT LIKE "") THEN (
  SELECT "" as INVALID
);
ELSE ( 
  SELECT --CONCAT(CAST(ROUND(sehbeteiligung,3) AS STRING), ' Mio.') as Sehbeteiligung
  ROUND(sehbeteiligung, 3) as Sehbeteiligung
  FROM `ndr-code-bi-dashboard.Quotenreport.${zielgruppen_und_facts}` 
  WHERE target_group = "BRD gesamt" AND show like "${Sendung}" AND year like "$Jahr"
);
