function formatDate(timestamp) {
  const date = new Date(timestamp);
  let day = ('0' + date.getDate()).slice(-2);
  let month = ('0' + (date.getMonth() + 1)).slice(-2);
  let year = date.getFullYear();
  return `${month}.${year}`;
}

const profilesData = {};
const timeValues = context.panel.data.series[0].fields.find(f => f.name === 'datum').values;

const colorPalette = [
  "rgb(24, 116, 255)", "rgb(255, 99, 71)", "rgb(60, 179, 113)",
  "rgb(255, 215, 0)", "rgb(138, 43, 226)", "rgb(255, 165, 0)",
  "rgb(75, 0, 130)", "rgb(173, 255, 47)", "rgb(47, 79, 79)"
];

context.panel.data.series.forEach((s) => {
  const profile = s.fields.find(f => f.name === 'profile').values;
  const impressions = s.fields.find(f => f.name === 'impressions').values;

  profile.forEach((profileName, index) => {
    if (!profilesData[profileName]) {
      profilesData[profileName] = [];
    }
    profilesData[profileName].push([timeValues[index], impressions[index]]);
  });
});

const series = Object.keys(profilesData).map((profileName, idx) => ({
  name: profileName,
  type: 'line',
  smooth: false,
  showSymbol: false,
  itemStyle: {
    color: colorPalette[idx % colorPalette.length]
  },
  data: profilesData[profileName],
}));

const xAxis = {
  type: 'category',
  data: timeValues,
  axisLabel: {
    hideOverlap: true,
  },
};

const yAxis = {
  type: 'value',
  min: '0',
  axisLabel: {
    formatter: function (value) {
      return value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, '.');
    }
  },
};

const legend = {
  orient: 'horizontal',
  left: 'center',
  bottom: '0%',
  itemGap: 10,
  textStyle: {
    fontSize: 16
  },
  // selected: Object.fromEntries(Object.keys(profilesData).map(profileName => [profileName, false]))

};

const tooltip = {
  trigger: 'axis',
  axisPointer: {
    type: 'cross'
  }
};

const grid = {
  left: '2%',
  right: '2%',
  top: '2%',
  bottom: '18%',
  containLabel: true,
};

return {
  backgroundColor: 'transparent',
  tooltip,
  legend,
  xAxis,
  yAxis,
  grid,
  series,
};

