/**
 * Update Time Range on Zoom
 */
context.panel.chart.on('datazoom', function (params) {
  const startValue = params.batch[0]?.startValue;
  const endValue = params.batch[0]?.endValue;
  context.grafana.locationService.partial({ from: startValue, to: endValue });
});


return {
  backgroundColor: 'transparent',
  tooltip: {
    trigger: 'axis',
    axisPointer: {
      type: 'cross'
    },
    formatter: function (params) {
      let tooltipContent = formatDate(params[0].axisValue) + '<br/>'; // Datumsformatierung
      params.forEach(function (item) {
        //let formattedValue = item.data[1].toString().replace(/\B(?=(\d{3})+(?!\d))/g, '.'); // Tausendertrennzeichen einfügen
        let number = Math.floor(item.data[1]);
        let formattedValue = new Intl.NumberFormat('de-DE').format(number);
        tooltipContent += item.marker + item.seriesName + ': ' + formattedValue + '<br/>';
      });
      return tooltipContent;
    }
  },
  legend: {
    orient: 'horizontal',
    left: 'center',
    bottom: '0%',
    itemGap: 40,
    textStyle: {
      fontSize: 16
    }
  },
  toolbox: {
    feature: {
      dataZoom: {
        yAxisIndex: 'none',
        icon: {
          zoom: 'path://',
          back: 'path://',
        },
      },
      saveAsImage: {},
    }
  },
  xAxis: {
    type: 'category',
    axisLabel: {
      hideOverlap: true,
      formatter: function (value) {
        const date = new Date(value);
        let day = ('0' + date.getDate()).slice(-2);
        let month = ('0' + (date.getMonth() + 1)).slice(-2);
        let year = date.getFullYear();
        return `${day}.${month}.${year}`;
      },
      //showMaxLabel: '20'
    },
  },
  yAxis: {
    type: 'value',
    min: '0',
    axisLabel: {
      formatter: function (value) {
        // Umwandeln des Werts in einen String und Ersetzen von Kommas durch Punkte
        return value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, '.');
      }
    },
  },
  grid: {
    left: '2%',
    right: '2%',
    top: '2%',
    bottom: 34,
    containLabel: true,
  },
  series,
};

