options.styles:
table {
  font-size: 40px;
  width: 1900px;
  border-collapse: collapse;
  table-layout: fixed;
}

th {
  font-size: 40px;
  font-weight: bold;
  text-align: left;
}

td {
  font-size: 40px;
  padding: 8px;
  height: 90px;
  vertical-align: middle;
  word-wrap: break-word;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

td.number {
  text-align: center;
}

#visits {
  text-align: center;
  width: 190px;
}

#content_id_sophora {
  width: 500px;
  vertical-align: middle;
  word-wrap: break-word;
  overflow: visible;
  white-space: normal;
}
