const series = context.panel.data.series.map((s) => {
  const sData = s.fields.find((f) => f.name === 'visits_past_5_minutes').values || s.fields.find((f) => f.type === 'number').values;
  let sTime = s.fields.find((f) => f.name === 'time').values || s.fields.find((f) => f.type === 'time').values;
  const type = s.fields.find((f) => f.name === 'type').values[0]
  if (type === "bar") {
    sTime = sTime.map(timestamp => {
      const date = new Date(timestamp);
      const day = date.getDate().toString().padStart(2, '0'); // Ensure two digits
      const month = (date.getMonth() + 1).toString().padStart(2, '0'); // Month is zero-based
      return `${day}.${month}`; // Format as DD.MM
    });
  }
  const refId = s.fields.find((f) => f.name === "refId").values[0]
  const glaettung = s.fields.find((f) => f.name === 'glaettung').values[0]
  const zeitraum = s.fields.find((f) => f.name === 'zeitraum').values[0]
  let color;
  console.log(glaettung, "Glaettung")
  console.log(zeitraum, "Zeitraum")
  if (glaettung === "Glätten" && zeitraum === "Zeitraum" && type === "line") {
    color = "#6aaa96"; // heute // Light Petrol
  } else if (glaettung === "Glätten" && zeitraum != "Zeitraum" && type === "line") {
    color = "#004c6d"; // gestern / dark Petrol
  } else if (glaettung != "Glätten" && zeitraum === "Zeitraum" && type === "line") {
    color = "#6aaa96"; // heute // Light Petrol
  } else if (glaettung != "Glätten" && zeitraum != "Zeitraum" && type === "line") {
    color = "#004c6d"; // gestern / dark Petrol
  }
  else if (type === "bar" && zeitraum === "Zeitraum") {
    color = "#6aaa96"; // heute // Light Petrol
  }
  else if (type === "bar" && zeitraum != "Zeitraum") {
    color = "#004c6d"; // gestern / dark Petrol
  }
  console.log("COLOR", color)
  return {
    name: refId,
    type: type,
    showSymbol: false,
    smooth: true,
    areaStyle: {
      opacity: 0.9,
    },
    itemStyle: {
      color: color,
    },
    lineStyle: {
      width: 1,
      color
    },
    data: sData.map((d, i) => [sTime[i], d.toFixed(2)]),
  };
});

const xAxisType = context.panel.data.series.map((s) => {
  const type = s.fields.find((f) => f.name === 'type').values[0]
  let xAxisType;
  if (type == "bar") {
    xAxisType = "category"
  }
  else {
    xAxisType = "time"
  }
  return xAxisType
})[0]

/**
