 */
setTimeout(() => context.panel.chart.dispatchAction({
  type: 'takeGlobalCursor',
  key: 'dataZoomSelect',
  dataZoomSelectActive: false,
}), 500);

/**
 * Update Time Range on Zoom
 */
context.panel.chart.on('datazoom', function (params) {
  const startValue = params.batch[0]?.startValue;
  const endValue = params.batch[0]?.endValue;
  context.grafana.locationService.partial({ from: startValue, to: endValue });
});


return {
  backgroundColor: 'transparent',
  tooltip: {
    trigger: 'axis',
    formatter: function (params) {
      let tooltipContent = '';
      params.forEach(function (item) {
        const date = new Date(item.data[0]);
        const tenMinutesInMilliseconds = 10 * 60 * 1000; // 10 minutes in milliseconds
        const date_start = new Date(date.getTime() - tenMinutesInMilliseconds);
        console.log("Date start", date_start)
        // const date_start = new Date(item.data[0]);
        //date_start.setMinutes(date.getMinutes() - 10)

        let hours = date.getHours().toString().padStart(2, '0');
        let minutes = date.getMinutes().toString().padStart(2, '0');
        let hours_start = date_start.getHours().toString().padStart(2, '0');
        let minutes_start = date_start.getMinutes().toString().padStart(2, '0');
        const timeString = hours_start + ':' + minutes_start;
        const timestring_end = hours + ':' + minutes;
        const intervalString = timeString + '-' + timestring_end

        let number = Math.floor(item.data[1]);
        let formattedNumber = new Intl.NumberFormat('de-DE').format(number);

        if (item.componentSubType === "bar") {
          tooltipContent += item.marker + item.seriesName + " Visits: " + formattedNumber + '<br/>';
        }
        else {
          tooltipContent += item.marker + item.seriesName + " " + intervalString + " Visits: " + formattedNumber + '<br/>';
        }
      });
      return tooltipContent;
    }
  },
  legend: {
    left: '0',
    bottom: '0',
    data: context.panel.data.series.map((s) => {
      const refId = s.fields.find((f) => f.name === "refId").values[0];
      return refId;
    }),
    textStyle: {
      color: 'rgba(128, 128, 128, .9)',
    },
  },
  toolbox: {
    feature: {
      dataZoom: {
        yAxisIndex: 'none',
        icon: {
          zoom: 'path://',
          back: 'path://',
        },
      },
      saveAsImage: {},
    }
  },
  xAxis: {
    type: xAxisType,
  },
  yAxis: {
    type: 'value',
    min: '0',
  },
  grid: {
    left: '2%',
    right: '2%',
    top: '2%',
    bottom: 24,
    containLabel: true,
  },
  series,
};

