function getRandomColor() {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}


const seriesData = data.series.map((s) => {
  const platformName = s.fields.find((f) => f.name === 'platform').values.buffer;
  const summeValue = s.fields.find((f) => f.name === 'summe').values.buffer;

  const res = platformName.map((name, index) => {
    const value = summeValue[index];
    let color;

    // Assign specific colors to certain platforms
    switch (name.toLowerCase()) {
      case 'ard audiothek':
        color = 'rgb(40, 115, 237)';
        break;
      case 'apple podcasts':
        color = 'rgb(166, 77, 204)';
        break;
      case 'spotify':
        color = 'rgb(89, 169, 77)';
        break;
      case 'other':
        color = 'rgb(255, 255, 255)';
        break;
      default:
        // Generate a random color for other platforms
        color = getRandomColor();
        break;
    }

    return { value, name, itemStyle: { color } };
  });

  return res;
}).flat();

